package main

import (
	"context"
	"fmt"

	"github.com/sacloud/schema/examples/go/sacloud"
)

const iconBody = ""

func main() {
	ctx := context.Background()
	client, err := sacloud.NewClientFromEnv()
	if err != nil {
		panic(err)
	}

	// createIcon
	created, err := createIcon(ctx, client)
	if err != nil {
		panic(err)
	}
	fmt.Printf("***** Create *****\n%#v\n\n", created)

	// updateIcon
	updated, err := updateIcon(ctx, client, created.ID)
	if err != nil {
		panic(err)
	}
	fmt.Printf("***** Update *****\n%#v\n\n", updated)

	// readIcon
	read, err := readIcon(ctx, client, updated.ID)
	if err != nil {
		panic(err)
	}
	fmt.Printf("***** Read *****\n%#v\n\n", read)

	// List
	listed, err := listIcon(ctx, client)
	if err != nil {
		panic(err)
	}
	fmt.Printf("***** List *****\n%#v\n\n", listed)

	// deleteIcon
	deleted, err := deleteIcon(ctx, client, updated.ID)
	if err != nil {
		panic(err)
	}
	fmt.Printf("***** Delete *****\n%#v\n\n", deleted)
}

func createIcon(ctx context.Context, client *sacloud.Client) (*sacloud.Icon, error) {
	res, err := client.CreateIcon(ctx, sacloud.CreateIconJSONRequestBody{
		Icon: sacloud.IconCreateRequestBody{
			Image: iconBody,
			Name:  "from-openapi-generator",
		},
	})
	if err != nil {
		return nil, err
	}

	createResponse, err := sacloud.ParseCreateIconResponse(res)
	if err != nil {
		return nil, err
	}
	return &createResponse.JSON201.Icon, nil
}

func updateIcon(ctx context.Context, client *sacloud.Client, id sacloud.ID) (*sacloud.Icon, error) {
	// updateIcon
	name := "from-openapi-generator-upd"
	tags := []string{"tag1", "tag2"}

	res, err := client.UpdateIconById(ctx, id, sacloud.UpdateIconByIdJSONRequestBody{
		Icon: sacloud.IconUpdateRequestBody{
			Name: &name,
			Tags: &tags,
		},
	})
	if err != nil {
		return nil, err
	}

	updateResponse, err := sacloud.ParseUpdateIconByIdResponse(res)
	if err != nil {
		return nil, err
	}
	return &updateResponse.JSON200.Icon, nil
}

func readIcon(ctx context.Context, client *sacloud.Client, id sacloud.ID) (*sacloud.Icon, error) {
	res, err := client.ShowIconById(ctx, id)
	if err != nil {
		return nil, err
	}

	readResponse, err := sacloud.ParseShowIconByIdResponse(res)
	if err != nil {
		return nil, err
	}
	return &readResponse.JSON200.Icon, err
}

func deleteIcon(ctx context.Context, client *sacloud.Client, id sacloud.ID) (*sacloud.Icon, error) {
	res, err := client.DeleteIconById(ctx, id)
	if err != nil {
		return nil, err
	}

	deleteResponse, err := sacloud.ParseDeleteIconByIdResponse(res)
	if err != nil {
		return nil, err
	}
	return &deleteResponse.JSON200.Icon, err
}

func listIcon(ctx context.Context, client *sacloud.Client) (*sacloud.Icons, error) {
	s := sacloud.Scope_user
	res, err := client.ListIcons(ctx, sacloud.IconFindRequest{
		Filter: &sacloud.IconFindFilter{
			Scope: &s,
			//Name: &[]string {
			//	"CentOS",
			//},
			//TagsName: &[]string {
			//	"category-os",
			//},
		},
		AdditionalProperties: nil,
	})
	if err != nil {
		return nil, err
	}

	listResult, err := sacloud.ParseListIconsResponse(res)
	if err != nil {
		return nil, err
	}

	return &listResult.JSON200.Icons, nil
}
